var moteApp = angular.module('moteApp', ['ngRoute']);

moteApp.config(function ($routeProvider) {
	$routeProvider
	.when ('/inicio',
		{
			templateUrl: 'views/home.html'
		})
	.when ('/step1',
		{
			controller:'motes1Controller',
			templateUrl: 'views/step1.html'
		})
		.when ('/step2',
		{
			controller:'motes2Controller',
			templateUrl: 'views/step2.html'
		})
		.when ('/castellanoToEuskara',
		{
			controller:'itzulpenController',
			controllerAs:'vm',
			templateUrl: 'views/itzulpenak.html',
			resolve: {
				languaje: function() { return "castellano"; }
			}
		})
		.when ('/euskaraToCastellano',
		{
			controller:'itzulpenController',
			controllerAs:'vm',
			templateUrl: 'views/itzulpenak.html',
			resolve: {
				languaje: function () { return "euskara"; }
			}
		})
	.otherwise({ redirectTo: '/inicio' });
});
(function () {

	'use strict';

	function ItzulpenController ($scope, itzulpenFactory, languaje) {
	
		var vm = this;
		vm.refreshItzulpenSet = refreshItzulpenSet;
		vm.toggleDisplay = toggleDisplay;

		if (languaje === "castellano") vm.keyIndex = ['A','B','C','D','E','F','G','H','I','J','K','L','M','N','Ñ','O','P','Q','R','S','T','U','V','W','X','Y','Z'];
		else vm.keyIndex = ['A','B','D','E','F','G','H','I','J','K','L','M','N','O','P','R','S','T','U','X','Z'];

		function refreshItzulpenSet (key) {
			if (!key) key = 'A';
			if (languaje === "castellano") vm.itzulpenak = itzulpenFactory.getCastellano(key);
			else vm.itzulpenak = itzulpenFactory.getEuskara(key);
		}

		function toggleDisplay (row) {
			row.display = !row.display;
		};

		refreshItzulpenSet();
	};

ItzulpenController.$inject = ['$scope', 'itzulpenFactory', 'languaje'];

	angular.module('moteApp')
		.controller('itzulpenController', ItzulpenController);
})();
moteApp.controller('motes1Controller', function ($scope, motesFactory) {
	$scope.motes = motesFactory.getMotes();

	$scope.anadirMote =  function () {
		if (!isNaN($scope.nuevoMote.popularity)) {
			var max = Math.max.apply(Math, motesFactory.getMotes().map(function(o){return o.priority;})) + 1;
			motesFactory.setMotes($scope.nuevoMote.name, max, parseInt($scope.nuevoMote.popularity));	
		}
	};
});
moteApp.controller('motes2Controller', function ($scope, motesFactory) {
	$scope.motes = motesFactory.getMotes();
});
(function () {

	'use strict';

	function ItzulpenNavbar () {

	 	return {
	    	restrict: 'E',
	    	templateUrl: 'views/navbar.html',
	    	scope: {},
	    	link: function ($scope) {
	    		$scope.navbarActive = navbarActive;

				function navbarActive(id) {
					$('.listanavbar li').removeClass('active');
					$('#nav'+id).addClass('active');
					$('#navb'+id).addClass('active');
				}
	    	}
		};			
	}


	ItzulpenNavbar.$inject = [];

	angular.module('moteApp')
		.directive('itzulpenNavbar', ItzulpenNavbar);

})();
moteApp.filter('range', function(){
    return function(items, property, min, max) {
      	return items.filter(function(item){
        	return item[property] >= min && item[property] <= max;
      	});
	};
});
